igg.site.onDocumentReady = function() {
  $(".i-project-card").each(function() {
    igg.site.refreshProjectCard($(this));
  });
  igg.externalService(function(){
    igg.js_widgets.sailthruHorizonIntegration();
  });
};

igg.site.refreshProjectCard = function($projectCard) {
  igg.site.setupDelayedImageLoad($projectCard);
  igg.site.ellipsizeProjectCardTagline($projectCard);
};

igg.site.setupDelayedImageLoad = function($projectCard) {
  var $imgContainer = $projectCard.find(".i-img");
  if ($imgContainer.length > 0) {
    var $img = $("<img />");
    $img.attr("src", $imgContainer.data("src"));
    $imgContainer.replaceWith($img);
  }
};

igg.site.startSpinning = function($button) {
  var $spinnerContainer = $("<div class='i-spinner-container'></div>");
  $button.after($spinnerContainer);

  var opt = {
    lines: 12, // The number of lines to draw
    length: 5, // The length of each line
    width: 4, // The line thickness
    radius: 10, // The radius of the inner circle
    color: $spinnerContainer.css("color"), // #rgb or #rrggbb
    speed: 1, // Rounds per second
    trail: 100, // Afterglow percentage
    opacity: 1 / 4,
    fps: 20
  };
  $spinnerContainer.spin(opt);
};

igg.site.stopSpinning = function($button) {
  $button.siblings(".i-spinner-container").remove();
};

// Ellipsize the project card tagline to fit inside the project card subject to flexible title/partner info
igg.site.ellipsizeProjectCardTagline = function($projectCard) {
  var $content = $projectCard.find(".i-content");
  var $title = $content.find(".i-title");
  var titleLineHeight = parseInt($title.css("line-height"));
  $title.dotdotdot({
    height: titleLineHeight*3,
    wrap: "letter",
    watch: "window"
  });

  var $tagline = $content.find(".i-tagline");
  var taglineHeight = $content.height() - $title.height();

  var $partner = $content.find(".i-partner-name");
  if ($partner.length > 0) {
    var partnerLineHeight = parseInt($partner.css("line-height"));
    $partner.dotdotdot({
      height: partnerLineHeight*1,
      watch: "window"
    });
    taglineHeight = taglineHeight - $partner.height() - parseInt($partner.css("margin-top"));
  }
  $tagline.css("max-height", taglineHeight);
  $tagline.dotdotdot({
    watch: 'window'
  });
};

igg.site.setupZeroClipboard = function(zeroClipboardSwf, $clipEl) {
  ZeroClipboard.config({
    moviePath: zeroClipboardSwf,
    forceHandCursor: true
  });
  var client = new ZeroClipboard($clipEl.get(0));
  client.on( "load", function(client) {
    client.on( "complete", function(client, args) {
      alert("Copied to the clipboard: " + args.text );
    });
  });

  client.on( 'noflash', function ( client, args ) {
    $clipEl.click(function() {
      var txt = $(this).attr('data-clipboard-text');
      prompt("Copy link, then click OK.", txt);
    });
  });
};

igg.site.addSimpleClientSideValidation = function($form) {
  $form.on('change', function() {
    $(this).validate({
      errorClass: "i-error"
    });
  });
};
