igg.site.CampaignPage = Backbone.View.extend({

  events: {
    "click .js-show-more": "onClickShowMore",
    "click .js-tab-link": "onClickTabLink",
    "submit #new_comment": "onSubmitNewComment",
    "click .js-delete-link": "onClickDeleteLink",
    "click .js-video-thumbnail": "onClickVideoThumbnail",
    "click .js-private-comment-only": "onClickCommentAppearancePrivateOnly",
    "click .js-main-contribute-button": "onClickMainContributeButton",
    "click .js-bottom-contribute-button": "onClickBottomContributeButton",
    "click .js-perks a": "onClickPerkDiv"
  },

  initialize: function (options) {
    this.zeroClipboardSwf = options.zeroClipboardSwf;
    this.currentTab = null;
    this.cachedTabs = {};
    this.facebook_app_id = options.facebook_app_id;
    igg.externalService(function() {
      ga('ec:addImpression', gon.ga_impression_data);
      ga('set', 'contentGroup1', 'Campaign');
      ga('send', 'pageview', gon.pageview_data.new_url);
      _gaq.push(['_trackEvent', 'Sticky Contribute - Control', 'View', '']);
    });
  },

  start: function () {
    this.$(".js-goal-info-popover").iggPopover({});

    this.$(".js-started-on-popover").iggPopover({
      placement: 'top'
    });
    this.router = new igg.site.CampaignPageRouter();

    var self = this;

    this.router.on("route:tab", function(tabString, anchor) {
      var $tab = self.findTab(tabString);

      if($tab.length > 0) {
        self.showTab($tab, anchor);
      }
      else {
        self.showTab(self.findTab("home"));
      }
    });

    Backbone.history.start();
  },

  scrollToAnchor : function(anchor) {
    var $anchor = this.$('.js-anchor[name="' + anchor + '"]');
    $(window).scrollTop($anchor.offset().top);
  },

  onClickTabLink: function (e) {
    e.preventDefault();
    var $newTab =  $(e.currentTarget).closest(".i-tab");
    var tabString = $newTab.data("tab-id");
    if (tabString !== this.currentTab) {
      this.router.navigate(tabString, {trigger: true});
    }
  },

  findTab: function(tabString) {
    return this.$('.i-tab[data-tab-id="' + tabString + '"]');
  },

  showTab: function ($newTab, anchor) {
    var self = this;

    this.currentTab = $newTab.data('tab-id');
    this.$(".i-tab").removeClass("i-selected");

    $newTab.addClass("i-selected");

    var $oldTabContent = this.$(".js-tab-content");
    if (this.cachedTabs[this.currentTab]) {
      $oldTabContent.replaceWith(this.cachedTabs[this.currentTab]);
      this.refreshJSForCurrentTab({cached: true});
      if(anchor) {
        this.scrollToAnchor(anchor);
      }
    } else {
      var $newTabContent = $("<div class='js-tab-content'></div>");
      $oldTabContent.replaceWith($newTabContent);
      $newTabContent.load($newTab.find('a').data("url"), function () {
        self.$(".js-tab-flash").html("");
        self.refreshJSForCurrentTab();
        if(anchor) {
          self.scrollToAnchor(anchor);
        }
      });
    }
  },

  refreshJSForCurrentTab: function(options) {
    if (typeof(options) === 'undefined') {
      options = {cached: false};
    }
    var self = this;
    setup_counters();
    if (this.currentTab === 'home') {
      self.setupPitchMedia();
      var $followLink = this.$('.js-follow');
      if ($followLink.length > 0) {
        this.followLinkView = new igg.site.FollowLinkView({el: $followLink});
      }
      if (!options.cached) {
        igg.externalService(function() {
          self.setupTwitterButton();
          self.setupFacebookShare();
          self.setupGplusButton();
          igg.site.setupZeroClipboard(self.zeroClipboardSwf, self.$(".js-clip"));
        });
        this.cachedTabs[this.currentTab] = this.$(".js-tab-content");
      }
    } else if (this.currentTab === 'activity') {
      setupRedactorForUpdatesTab();
    } else if (this.currentTab === 'gallery') {
      this.setupGalleryElements();
    }
  },

  setupPitchMedia: function () {
    new igg.site.VideoOverlay({el: this.$("#pitchvideo")});
  },

  onClickShowMore: function (e) {
    e.preventDefault();
    var self = this;
    this.$(".js-tab-content").load($(e.target).attr("href"), function() {
      self.refreshJSForCurrentTab();
    });
  },

  onClickDeleteLink: function (e) {
    e.preventDefault();
    var self         = this;
    var $link        = $(e.target);
    var confirm_text = $link.data('confirm-text');

    if(!confirm_text || confirm(confirm_text)) {
      $.ajax($link.attr("href"), {
        type: "DELETE",
        complete: function (jqXHR) {
          self.onAjaxTabResponse(jqXHR.responseText, jqXHR.status, jqXHR);
        }
      });
    }
  },

  onSubmitNewComment: function (e) {
    e.preventDefault();
    var self = this;
    var $form = $(e.target);
    $.post($form.attr("action"), $form.serialize(),function (data, status, jqXHR) {
      self.onAjaxTabResponse(data, status, jqXHR);
    }).fail(function (jqXHR) {
        if (jqXHR.status == 401) {
          self.$el.off();
          $form.submit();
        }
      });
  },

  onAjaxTabResponse: function (data, status, jqXHR) {
    var flashHeader = jqXHR.getResponseHeader('X-Flash-Messages');
    var self = this;
    var flashMessages;
    if (flashHeader) {
      flashMessages = $.parseJSON(flashHeader);
    }
    this.$(".js-tab-flash").html("");
    if (flashMessages && flashMessages.error) {
      showFlashMessage({flashContainerEl: this.$(".js-tab-flash"), alertLevel: "error", messageText: flashMessages.error[0]});
    } else {
      this.$(".js-tab-content").html(data);
      self.refreshJSForCurrentTab();
      for (var key in flashMessages) {
        showFlashMessage({flashContainerEl: this.$(".js-tab-flash"), alertLevel: "notice", messageText: flashMessages[key][0]});
      }
    }
  },

  setupFacebookShare: function () {
    try{
      FB.XFBML.parse();
    }catch(ex){}
  },

  setupTwitterButton: function () {
    function twitterSetup(d, s, id) {
      //this code is taken directly from twitter and modified to support AJAX reload

      var js, fjs = d.getElementsByTagName(s)[0];
      if (!d.getElementById(id)) {
        js = d.createElement(s);
        js.id = id;
        js.src = "//platform.twitter.com/widgets.js";
        fjs.parentNode.insertBefore(js, fjs);
      } else {
        if (typeof(twttr) !== 'undefined') {
          twttr.widgets.load();
        }
      }
    }

    twitterSetup(document, "script", "twitter-wjs");
  },

  setupGplusButton: function() {
    if (typeof(gapi) !== 'undefined') {
      gapi.plusone.go();
    }
  },

  setupGalleryElements: function () {
    if (this.$(".js-thumbnails").length > 0) {
      var gallery = this.$(".js-thumbnails").galleriffic({
        imageContainerSel: '#image_display',
        numThumbs: 50,
        preloadAhead: -1,  //preload everything.
        renderNavControls: false,
        enableKeyboardNavigation: false,
        captionContainerSel: '#image_caption' //render captions within the #image_caption div
      });
    }
  },

  onClickVideoThumbnail: function (e) {
    e.preventDefault();
    var $thumbnail = $(e.currentTarget);
    this.$(".js-video-player").load($thumbnail.attr('href'));
  },

  onClickCommentAppearancePrivateOnly: function(e){
    $(e.currentTarget).prop('checked', true);
    showFlashMessage({
      messageText: I18n.t('you_must_contribute_to_make_public_comment'),
      flashContainerEl: $('.js-tab-flash')
    });
  },

  onClickMainContributeButton: function(e) {
    igg.externalService(function(){
      _gaq.push(['_trackEvent', 'Sticky Contribute - Control', 'Click', 'Main Contribute']);
    });
  },

  onClickBottomContributeButton: function(e) {
    igg.externalService(function(){
      _gaq.push(['_trackEvent', 'Sticky Contribute - Control', 'Click', 'Bottom Contribute']);
    });
  },

  onClickPerkDiv: function(e) {
    var name = $(e.currentTarget).data('perk-name');
    var addPerkData = $.extend({}, gon.ga_impression_data, {'dimension1': 'Contributor', 'coupon': name });
    igg.externalService(function(){
      ga('ec:addProduct', addPerkData);
      ga('ec:setAction', 'detail', {});
      ga('send', 'pageview', gon.pageview_data.new_url);
      _gaq.push(['_trackEvent', 'Sticky Contribute - Control', 'Click', 'Perk']);
    });
  },
});
