igg.site.ExplorePage = Backbone.View.extend({
  events : {
    'click .js-show-more' : 'onShowMoreClick',
    'click .i-project': 'onClickProjectCard'
  },

  initialize: function(options) {
    this.location = options.location || window.location;
    this.gon = options.gon || window.gon;
    igg.externalService(function() {
      ga('set', 'contentGroup1', 'Discovery');
      ga('send', 'pageview', gon.pageview_data.new_url);
    });
  },

  start: function() {
    var self = this;

    this.$showMoreLink = this.$('.js-show-more');
    this.$projectsList = this.$(".i-project-cards");
    this.$fetchCount   = this.$('.js-fetch-count');
    this.$scrollTop    = this.$('.js-scroll-top');

    $(window).scroll(function() {
      self.onWindowScroll.apply(self, arguments);
    });

    var $dropdownHandle = this.$(".i-dropdown-handle");
    $dropdownHandle.attr("data-toggle", "dropdown");
    $dropdownHandle.dropdown();

    var $countrySelect = this.$("#filter_country");
    $countrySelect.iggSelect2();

    var $advancedFilterSelect = this.$(".i-select");
    var $accordion = this.$(".i-accordion");
    var accordionHeight = ($accordion.height() > 0 ? $accordion.height() : 584); // fixes responsive web behavior when sidebar starts out hidden
    var accordionPadding = $accordion.css("paddingTop");
    var accordionOpen = !$accordion.hasClass("i-offscreen-accordion");
    if (accordionOpen) {
      $advancedFilterSelect.find(".i-icon").css("transform", "rotate(180deg)");
      $accordion.height(accordionHeight); // need to lock it down for the first click transition to be smooth
      $advancedFilterSelect.find(".i-text").text(I18n.t("hide_filters"));
    } else {
      $advancedFilterSelect.find(".i-text").text(I18n.t("show_more_filters"));
    }

    $advancedFilterSelect.click(function() {
      accordionOpen = !accordionOpen;
      if (accordionOpen) {
        $accordion.css({height: 0, paddingTop: 0, opacity: 0, position: "static"});
        $accordion.removeClass("i-offscreen-accordion");
        $advancedFilterSelect.find(".i-text").text(I18n.t("hide_filters"));
        $advancedFilterSelect.find(".i-icon").transition({rotate: 180});
        $accordion.transition({opacity: 100, paddingTop: accordionPadding, height: accordionHeight});
      } else {
        $advancedFilterSelect.find(".i-text").text(I18n.t("show_more_filters"));
        $advancedFilterSelect.find(".i-icon").transition({rotate: 0});
        $accordion.transition({opacity: 0, paddingTop: 0, height: 0}, function() {
          $accordion.addClass("i-offscreen-accordion");
        });
      }
    });

    this.restoreShowMoreState();
  },

  onWindowScroll : function(e) {
    this.$scrollTop.val($(window).scrollTop());
  },

  restoreShowMoreState : function() {
    var self = this;

    if(this.$fetchCount.val()) {
      this.loadProjects(parseInt(this.$fetchCount.val()), function() {
        $(window).scrollTop(self.$scrollTop.val());
      });
    }
    else {
      this.$fetchCount.val(12);
    }
  },

  onShowMoreClick : function(e) {
    e.preventDefault();
    this.loadProjects(parseInt(this.$fetchCount.val()) + 12);
  },

  loadProjects : function(count, callback) {

    var self = this;
    var url  = this.$showMoreLink.attr("href") + "&per_page=" + count;

    this.$showMoreLink.hide();
    igg.site.startSpinning(this.$showMoreLink);

    this.$projectsList.load(url + ' .project-card-with-friend-list', function() {

      self.$projectsList.find(".i-project-card").each(function() {
        igg.site.refreshProjectCard($(this));
      });

      igg.site.stopSpinning(self.$showMoreLink);
      if($('.i-project-cards > .project-card-with-friend-list').length === count) {
        self.$showMoreLink.show();
      }

      self.$fetchCount.val(count);

      if(callback) {
        callback.call(this);
      }
    });
  },

  getUrlVars: function(urlString) {
    var hash = {}, keyVal;
    var params = urlString.split('?')
    if (params.length < 2) { return {}; }
    params = params[1].split('&');
    for(var i = 0; i < params.length; i++){
      keyVal = params[i].split('=');
      hash[keyVal[0]] = keyVal[1];
    }
    return hash;
  },

  onClickProjectCard: function(e){
    e.preventDefault();

    var self = this;
    var eventData = {index: $(e.currentTarget).closest('.project-card-with-friend-list').data('explore-index')};

    var urlParams = self.getUrlVars(self.location.href);
    if (urlParams['filter_quick']) { eventData.tab = urlParams['filter_quick']; }
    if (self.gon.search_term) { eventData.search_term = self.gon.search_term }

    $.ajax({
      url: '/analytics/events',
      method: 'POST',
      data: {
        event: 'search_results_click',
        event_data: eventData
      }
    }).always(function () {
      var href = $(e.currentTarget).attr('href');
      self.location.href = href;
    });
  }


});



