igg.site.varyLocationFieldsBasedOnCountry = function($countryEl,
                                                     $statePlacementDiv,
                                                     $stateDiv,
                                                     $provinceDiv,
                                                     $addressLine1Label,
                                                     $addressLine2Label,
                                                     $zipCodeLabel,
                                                     $hiddenArea)
{
  function varyFieldsForCountryValue(countryValue) {
    $hiddenArea.append($stateDiv);
    $hiddenArea.append($provinceDiv);
    if (countryValue === 'CTRY_CA') {
      $statePlacementDiv.append($provinceDiv);
      $statePlacementDiv.show();
      if ($addressLine1Label != null && $addressLine2Label != null) {
        $addressLine1Label.text(I18n.t('address_line_1'));
      }
      if ($addressLine2Label != null) {
        $addressLine2Label.text(I18n.t('address_line_2'));
      }
      if($zipCodeLabel) {
        $zipCodeLabel.text(I18n.t('postal_code'));
      }
    } else if (countryValue === 'CTRY_US') {
      $statePlacementDiv.append($stateDiv);
      $statePlacementDiv.show();
      if ($addressLine1Label != null && $addressLine2Label != null) {
        $addressLine1Label.text(I18n.t('address_line_1'));
      }
      if ($addressLine2Label != null) {
        $addressLine2Label.text(I18n.t('address_line_2'));
      }
      if($zipCodeLabel) {
        $zipCodeLabel.text(I18n.t('zip_code'));
      }
    } else {
      $statePlacementDiv.hide();
      if ($addressLine1Label != null && $addressLine2Label != null) {
        $addressLine1Label.text(I18n.t('address_line_1_house_number_name'));
      }
      if ($addressLine2Label != null) {
        $addressLine2Label.text(I18n.t('address_line_2_street_apt_etc'));
      }
      if($zipCodeLabel) {
        $zipCodeLabel.text(I18n.t('postal_code'));
      }
    }
  }

  $countryEl.change(function() {
    varyFieldsForCountryValue($(this).val());
  });
  varyFieldsForCountryValue($countryEl.val());
};
