/** Retrofitting old highly used JS functions into the new site refresh **/
function hideFlashMessage(args) {
  var $flashContainerEl = $(".js-flash-container");
  if (typeof(args) !== "undefined" &&
    typeof(args.flashContainerEl) !== "undefined") {
    $flashContainerEl = args.flashContainerEl;
  }
  $flashContainerEl.html("");
}
function showFlashMessage(args) {
  var $flashEl = $('<div class="i-flash"><span></span><a class="js-close i-icon i-glyph-icon-30-close"></a></div>');

  var flashClass;
  switch (args.alertLevel) {
    case 'info':
    case 'notice':
    case 'message':
      flashClass = 'i-flash-info';
      break;
    default:
      flashClass = 'i-flash-error';
  }

  $flashEl.addClass(flashClass);
  $flashEl.find("span").html(args.messageText);

  var $flashContainerEl = $(".js-flash-container");
  if (typeof(args.flashContainerEl) !== "undefined") {
    $flashContainerEl = args.flashContainerEl;
  }

  $flashContainerEl.empty();
  $flashContainerEl.append($flashEl);
  new igg.site.Flash({el: $flashEl}).start();
}
;

