igg.site.GoalInput = Backbone.View.extend({
  events: {
    'click li:not(.i-disabled)': 'onCurrencyClick',
    'keypress .js-goal-value': 'removeNonNumericCharacters'
  },

  initialize: function (options) {
    this.list_elements = this.$('li');
    this.currency_label = this.$('.js-currency-label');
    this.currency_value = this.$('.js-currency-value');
    this.selected = null;

    var self = this;
    this.list_elements.each(function () {
      var currency = _.findWhere(self.model, {"iso_code": $(this).data("iso-code")});
      if (currency == null) {
        throw("Could not find currency object for iso code " + $(this).data("iso-code"));
      }
      $(this).data("currency", currency);
      if (currency.iso_code === "USD") {
        self.$usItem = $(this);
      }
    });

    this.render();
  },

  isUS: function () {
    return this.selected && this.selected.data("currency").iso_code === "USD"
  },

  render: function () {
    var selected_element = null;
    var currency = this.currency_value.val();

    if (currency) {
      selected_element = this.list_elements.filter(function () {
        return $(this).data('currency').iso_num.toString() === currency;
      }).first();
    }
    else {
      selected_element = this.list_elements.first();
    }

    this.selectCurrency(selected_element);
  },

  onCurrencyClick: function (e) {
    this.selectCurrency($(e.currentTarget));
  },

  selectCurrency: function (element) {
    this.list_elements.removeClass('i-selected');
    element.addClass('i-selected');

    this.selected = element;
    var currency = this.selected.data("currency");
    this.currency_label.text(currency.symbol);
    this.currency_value.val(currency.iso_num);
    this.trigger("change:currency");
  },

  disableNonUS: function () {
    this.list_elements.addClass("i-disabled");
    this.$usItem.removeClass("i-disabled");
  },

  enableAll: function () {
    this.list_elements.removeClass("i-disabled");
  },

  removeNonNumericCharacters: function (event) {
    if ((event.which < 48 || event.which >= 58) && event.which != 0 && event.which != 8) {
      event.preventDefault();
    } // prevent if not number. allow arrows and backspace, which trigger keypress in Firefox.
  }
});

