            url = sel.anchorNode.parentNode.href;
            text = sel.anchorNode.parentNode.text;
            target = sel.anchorNode.parentNode.target;

            if (sel.toString() === '') {
              this.insert_link_node = sel.anchorNode.parentNode;
            }
