igg.projects.showCurrency = function(amount) {
  var example = gon.currency.example;
  var exampleDelimiterIndex = example.indexOf("999") - 1;
  var exampleDelimiter = example.slice(exampleDelimiterIndex, exampleDelimiterIndex + 1);
  var exampleAmount = example.slice(exampleDelimiterIndex - 1, exampleDelimiterIndex + 4);

  var amountString = amount.toString();
  var currentDelimiterIndex = amountString.length - 3;
  while (currentDelimiterIndex > 0) {
    amountString = amountString.slice(0, currentDelimiterIndex) +
      exampleDelimiter + amountString.slice(currentDelimiterIndex, amountString.length);
    currentDelimiterIndex -= 3;
  }

  return example.replace(exampleAmount, amountString);
}
;



