/* save with ajax elements within the group class onblur */
function updateAjaxFieldsLiveAsTheyBlur() {
  jQuery(':input.ajax,.ajax :input:not(.no-ajax)').blur(function() {
    var this_ = jQuery(this);

    if (this_.data("processing")) {
      //only process once
    } else if (this_.data("changed")) {
      //      console.log(this_);
      this_.data("processing", true);

      jQuery.ajax({
        type: 'PUT',
        url: location.href.replace(/\/[a-z]*$/, "") ,
        data: jQuery('#id').add('#project_id').add(this_).serializeArray(),
        dataType:'json',
        success: function(data, textStatus, jqXHR) {
          clearErrors();
          this_.removeData("changed")
        },
        error: function(jqXHR, textStatus, errorThrown) {
          clearErrors();

          if (jqXHR.status == 422) {
            pledge = jQuery.parseJSON(jqXHR.responseText).pledge;
            setFieldErrors(this_, pledge, pledge.errors);
          } else {
//            showFlashMessage( { messageText: system_error_general, alertLevel: "error" });
          }
        },
        complete: function(jqXHR, textStatus) {
          this_.removeData("processing");
        }
      });
    }
  });
}

/* remove error messages and classes */
function clearErrors() {
  jQuery('.error, .field_with_errors').removeData('error').removeClass('error field_with_errors');
}

/* setup qtip and display the error */
function setFieldErrors(ele, data, errors) {

  /* go thru each error and add error classes and data-error attributes */
  jQuery.each(errors, function(i, val) {
    var field = val[0];
    var msg = val[1];
    var human_field = val[2] || val[0]
    if (jQuery(ele).filter('[name*="['+field+']"]').length>0)
      addFieldError(ele, msg);
  });

  if (jQuery('.error').length>0) {
    var group = jQuery(ele).closest('.group');
    if (group.length==0) group = ele;
    var x1 = group.position().left + group.width() + 20;

    /* initialize qtip for error tips */
    jQuery('.error').qtip({
      content: {
        text: function() {
          var html = "<ul class='no-list-style'>";
          var error = jQuery.makeArray(jQuery(this).data('error'));
          jQuery.each(error, function(i, val) {
            html = html+"<li>"+val+"</li>";
          });
          html = html +"</ul>";
          return html;
        }
      },
      show: {
        ready: true
      },
      hide: {
        event: 'click'
      },
      position: {
        my: 'center left',
        at: 'center left',
        adjust: {
          x: x1
        }
      },
      style: {
        width: 200,
        classes: 'ui-tooltip-shadow ui-tooltip-tipsy ui-toolip-error',
        tip: {
          height: 15,
          width: 7
        }
      }
    });
  }
}

/* go thru each error and add error classes and data-error attributes */
function addFieldError(ele, value) {
  /* set the error class and add a div with error msg */
  ele.addClass('error');
  l_ele = jQuery("label[for='"+jQuery(ele).attr("id")+"']");
  l_ele.addClass('field_with_errors');
  var error = ele.data('error');

  /* if an error already exists for this field append this error */
  if (jQuery.isArray(error)) {
    error.push(value);
  } else {
    error = jQuery.makeArray(value);
  }
  ele.data('error', error);

  ele.focus();
}
;



