function setupButtonSpinner(ele) {
  /* find or create spinner */
  var spinContainer = ele.next(".spinner-container");
  if (spinContainer.length==0) {
    spinContainer = $("<div class='spinner-container'></div>");
    spinContainer.hide();
    ele.after(spinContainer);
  }

  // clone the element to determine the width/height of hidden elements
  var clone = ele.clone();
  clone.css("visibility","hidden");
  $('body').append(clone);

  // font must be loaded to accurately calculate the width, so make sure method is called on load
  var width = clone.outerWidth(true);
  var height = clone.outerHeight(true);
  clone.remove();

  /* move the spinner div to the middle of the button */
  spinContainer.css('left', width / 2 - spinContainer.outerWidth() / 2 + 'px');

  /* set the width/height of button so that the text can be removed when spinning */
  ele.data("text", ele.text());
  ele.css("width", width + "px");
  ele.css("height", height + "px");

  // set color of spinner to background color of button
  var color = ele.css("color");
  spinContainer.css("color", color);
  return spinContainer;
}

function setupFileSpinner(ele) {
  /* find or create spinner */
  var button = ele.find(".cabinet .button-text");
  return setupButtonSpinner(button);
}
;


function stylizeFileInputs($fileInputs) {
  $fileInputs.each(function() {
    // when displaying button text instead of a background image set the cabinet width/height
    $(this).closest("label.cabinet").find(".button-text").each(function() {
      // clone the element to determine the width/height of hidden elements
      var clone = $(this).clone();
      clone.css("visibility","hidden");
      $('body').append(clone);

      // font must be loaded to accurately calculate the width, so make sure method is called on load
      var w = clone.outerWidth(true);
      var h = clone.outerHeight(true);

      clone.remove();
      $(this).closest("label.cabinet").css("width", w).css("height", h);
      $(this).closest(".i-upload-button").css("height", h);
    });

    SI.Files.stylize(this);
    $(this).show();
    $(this).removeClass("i-hidden");
  });
}

function setupStylizeAjaxForm(form, container, path, url, callbacks) {
  // find or create spinner container
  var spinContainer = setupFileSpinner(container);
  var button = container.find(".button-text");

  if (form.length>0) {
    form.ajaxForm({dataType: 'json',
      beforeSubmit: function(a,f,o) {
        spinContainer.show().spin(spinnerDefaultOptions);
        button.html("&nbsp;");
      },
      success: function(data, textStatus, jqXHR) {

        var obj = null;

        // the path argument can either be a single string
        // or a list of strings representing a path
        if(typeof path === 'object') {
          obj = data;
          for(var i = 0; i < path.length; ++i) {
            obj = obj[path[i]];
          }
        }
        else {
          obj = data[path];
        }

        onStylizeAjaxFormSuccess(obj[url], container);
        if(callbacks && callbacks.success) {
          callbacks.success.call(this, data, container);
        }
      },
      complete: function(jqXHR, textStatus){
        onStylizeAjaxFormComplete(button, spinContainer);
      },
    });
  }
}

function onStylizeAjaxFormSuccess(url, container) {
  if (url) {
    if ((obj = container.find('img')).length) {
      var secure_url = url.replace("http://","https://");
      obj.attr('src', secure_url);
    }
  }
}

function onStylizeAjaxFormComplete(button, spinContainer) {
  var buttonText = button.data("text");
  button.text(buttonText);
  spinContainer.spin(false).hide();
}

function stylizeAjaxFormSubmit(form, container) {
  var label = container.find("label");
  var file = label.find('input[type="file"]');

  form.children('input[type="file"]').remove();
  file.appendTo(form).addClass('hidden-file-field');
  form.submit();
  file.clone(true).appendTo(label).removeClass('hidden-file-field');
  form.find('input[type="file"]').removeAttr("id");
}
